const StudentForm=({
    index,
    addTag


})=>{

const[newTag, setNewTag]= useState('');

const changeTag=(e)=>{

    setNewTag(e.target.value);

}

    const onKeyUp=(event)=>{
        event.preventDefault();
    if (event.charCode === 13) {
        addTag(newTag, index);
      setNewTag("");
    }
  }

return(
   
              <form 
            //   onSubmit={e=>{
