            //     setNewTag("");
            //   }}
              
              ><input type="text" placeholder="add tag"  value={newTag} onKeyPress={onKeyUp} onChange={changeTag} id="" className="form-control" 
        ></input><input type="submit" ></input> </form>              
             

)

}

export default StudentForm;
