import logo from './logo.svg';
import React, {useState, useEffect} from "react";
import axios from 'axios';
import './App.css';
import Spinner from 'react-bootstrap/Spinner';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function App() {

  const [studentInfo, setStudentInfo] = useState([]);
  const [studentFilter, setStudentFilter]  = useState([]);
  const [loading, setLoading] = useState(false)

const fetchStudents= async (url)=>{
  // axios({
  //   method: 'get',
  //   url: url


  // }).then((response)=> 
  // setStudentInfo(response.data.students)).then(()=>console.log(studentInfo[0]))



const data= await fetch(url).then((response)=> response.json()).then((res)=>{res.map((student)=>{[...student, {tag:[]}]})  }).then((students)=>{
  setStudentInfo(students.students); 
  setStudentFilter(students.students)}).then((students)=>{

//  fetchContent('i');


setLoading(true);

console.log(loading);
console.log(studentInfo);
}).then(()=>{ console.log(studentFilter)})



// .then(()=>console.log(studentInfo))

}

const fetchContent =  (str)=> {

  // const array= []
  const result = studentInfo.filter((student)=>{
    // const fullName= `${student.firstName} ${student.lastName}`.toLowerCase();
  return student.firstName.toLowerCase().includes(str)||student.lastName.toLowerCase().includes(str)


  })




console.log(result);


 setStudentFilter(result);

 console.log(studentFilter);
}


const changeFilter =(e)=>{

  fetchContent(e.target.value)

}

const average= (arr)=>{

  return arr.reduce((a,b)=> Number(a)+Number(b), 0)/arr.length
}

useEffect(()=>{

  fetchStudents('https://api.hatchways.io/assessment/students');


},[]);


  return (
     <div className="App"> {loading? 
      <Container>


        <img src={logo} className="App-logo" alt="logo" />
        {/* <p> */}
        <div className="form-group row"><input className="form-control form-control-border mb-2 mt-2" name="searchStudent" id="searchStudent" type="text"  onChange={changeFilter} placeholder="Search Name"/></div>

          {studentFilter? studentFilter.map((student,index)=><div className="row service-box-wrap" key={index}>
            <div className="col-sm-3 col-md-3 col-lg-3 col-xl-3 d-flex" ><img className="rounded-circle border border-secondary mx-auto" src={student.pic}/></div>
            <div className="col-sm-9 col-md-9 col-lg-9 col-xl-9 ">
              <div className="h3"><p>{student.firstName} {student.lastName}</p></div>
              <div>Email: {student.email}</div>
              <div>Company: {student.company}</div>
              <div>Skill: {student.skill}</div> 
              <div>Average: {average(student.grades).toFixed(2)}%</div>                  
              </div> </div>):'Loading'}

          {studentInfo[0].city}
          Edit <code>src/App.js</code> and save to reload.
        {/* </p> */}
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </Container>: <Spinner animation="border" variant="info" />}
    </div>
  );
}

export default App;
