import logo from './logo.svg';
import React, {useState, useEffect} from "react";
import axios from 'axios';
import './App.css';
import Spinner from 'react-bootstrap/Spinner';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function App() {

  const [studentInfo, setStudentInfo] = useState();
  const [loading, setLoading] = useState(false)

const fetchStudents= async (url)=>{
  // axios({
  //   method: 'get',
  //   url: url


  // }).then((response)=> 
  // setStudentInfo(response.data.students)).then(()=>console.log(studentInfo[0]))



const data= await fetch(url).then((response)=> response.json()).then((students)=>{setStudentInfo(students.students);
setLoading(true);
console.log(loading);
console.log(studentInfo)}
)

// .then(()=>console.log(studentInfo))

}

const average= (arr)=>{

  return arr.reduce((a,b)=> Number(a)+Number(b), 0)/arr.length
}

useEffect(()=>{

  fetchStudents('https://api.hatchways.io/assessment/students');


},[]);


  return (
     <div className="App"> {loading? 
      <Container fluid>


        <img src={logo} className="App-logo" alt="logo" />
        {/* <p> */}
        <form><input type="text"></input></form>
          {studentInfo? studentInfo.map((student,index)=><div className="row service-box-wrap" key={index}>
            <div className="col-sm-3 col-md-3 col-lg-3 col-xl-3 d-flex" ><img className="rounded-circle border border-secondary mx-auto" src={student.pic}/></div>
            <div className="col-sm-9 col-md-9 col-lg-9 col-xl-9 ">
              <div className="h3"><p>{student.firstName} {student.lastName}</p></div>
              <div>Email: {student.email}</div>
              <div>Company: {student.company}</div>
              <div>Skill: {student.skill}</div> 
              <div>Average: {average(student.grades).toFixed(2)}%</div>                  
              </div> </div>):'Loading'}

          {studentInfo[0].city}
          Edit <code>src/App.js</code> and save to reload.
        {/* </p> */}
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </Container>: <Spinner animation="border" variant="info" />}
    </div>
  );
}

export default App;
