},[]);


  return (
     <div className="App"> {loading? 
      <Container fluid>
      <header className="App-header">

        <img src={logo} className="App-logo" alt="logo" />
        {/* <p> */}

          {studentInfo? studentInfo.map((student,index)=><div className="row" key={index}><div className="col-6" >{student.city}</div></div>):'Loading'}

          {studentInfo[0].city}
